function farmHats()
    while farmhats == true do
        wait()
        game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
		game:GetService("ReplicatedStorage").Wear:FireServer()
	
	

	



    end
end

function farmCoins()
    while farmcoins == true do
        for _,v in pairs(game:GetService("Workspace").CoinAndGems:GetChildren()) do 
            if farmcoins == false then return end
            for _,e in pairs(v:GetChildren()) do 
               if farmcoins == false then return end		
                if e.Name == "Coin" then	
                   wait()		
                    e.CFrame = game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame
                end		
            end
        end
    end
end

function farmGems()
    game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame = CFrame.new(-36871.4414, 665.017944, -193.563797, 0.999993622, 2.82921686e-08, 0.00356981135, -2.78989365e-08, 1, -1.10204468e-07, -0.00356981135, 1.10104175e-07, 0.999993622)
    while farmgems == true do
        for _,v in pairs(game:GetService("Workspace").CoinAndGems:GetChildren()) do 
            if farmgems == false then return end
            if v.Name == "Gem" then	
                if farmgems == false then return end
                wait(.00000000000001)		
                v.CFrame = game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame
            end			
        end
    end
end

function sellCoins()
    while sellcoins == true do
        wait(.1)
        game:GetService("Workspace").ScriptedParts.SellHats2.Touch.CFrame = game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame	
    end
end

function sellGems()
    game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame = CFrame.new(-36871.4414, 665.017944, -193.563797, 0.999993622, 2.82921686e-08, 0.00356981135, -2.78989365e-08, 1, -1.10204468e-07, -0.00356981135, 1.10104175e-07, 0.999993622)
    while sellgems == true do
        wait(.1)
        game:GetService("Workspace").ScriptedParts.SpaceSell.CFrame = game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame
    end
end